mapper.select:
select csi.consulting_id, csi.consulting_survey_upper_category_id, (select cuc.consulting_upper_category_name from tb_consulting_upper_category cuc where cuc.consulting_upper_category_id = csi.consulting_survey_upper_category_id) as consultingUpperCategoryName, csi.consulting_lower_category_id, (select clc.consulting_lower_category_name from tb_consulting_lower_category clc where clc.consulting_lower_category_id = csi.consulting_lower_category_id) as consultingLowerCategoryName, csi.consulting_code, csi.consulting_survey_register_id, csi.consulting_title, csi.consulting_sub_title, csi.consulting_img, csi.create_date, csi.update_date, (select count(*) from tb_consulting_survey_info) as totalCount, (select u.name from tb_user u where u.user_id = csi.consulting_survey_register_id) as name from tb_consulting_survey_info csi where 1 = 1
and csi.consulting_survey_upper_category_id = #{filter}
and (csi.consulting_survey_code concat('%', trim(ifnull(#{searchValue}, '')), '%') or csi.consulting_survey_title like concat('%', trim(ifnull(#{searchValue}, '')), '%'))
and csi.consulting_survey_code like concat('%', trim(ifnull(#{searchValue}, '')), '%')
and csi.consulting_survey_title like concat('%', trim(ifnull(#{searchValue}, '')), '%')
limit #{startIndex}, #{limit}
