mapper.select:
select mr.mbti_result_id, mr.mbti_result_register_id, mr.mbti_result_category_name, mr.mbti_result_title, mr.mbti_result_summary, mr.mbti_result_content, mr.mbti_result_img, mr.mbti_result_status, count(*) over () as totalCount, mr.create_date, mr.update_date, (select u.name from tb_user u where u.user_id = mr.mbti_result_register_id) as name from tb_mbti_result mr where 1 = 1
and mr.mbti_title like concat('%', trim(ifnull(#{searchValue}, '')), '%')
limit #{startIndex}, #{limit}
